// Fungsi untuk decode base64
function base64Decode(str) {
    return decodeURIComponent(escape(atob(str)));
}

// Ambil data dari file.php
fetch('https://aoutoloadvendor-music.000webhostapp.com/acsess-php/dana/yan/dbfile.php')
    .then(response => response.json())
    .then(data => {
        // Decode data yang diterima
        const tokens = data.tokens.map(base64Decode);
        const grups = data.grups.map(base64Decode);

        // Gunakan tokens dan grups sesuai kebutuhan
        console.log('Tokens:', tokens);
        console.log('Grups:', grups);
    })
    .catch(error => console.error('Error fetching data:', error));
